import Link from "next/link";
import type { Metadata } from "next";
import { client } from "@/sanity/lib/client";
import { all{{.PascalCasePlural}}Query } from "@/sanity/lib/{{.KebabCasePageType}}.queries";

export const metadata: Metadata = {
  title: "{{.PascalCasePlural}}",
  description: "All {{.LowerCasePlural}}"
};

export default async function {{.PascalCasePageType}}IndexPage() {
  const items = await client.fetch(all{{.PascalCasePlural}}Query);

  if (!items?.length) {
    return (
      <main className="container mx-auto p-6">
        <h1 className="text-2xl font-semibold">{{.PascalCasePlural}}</h1>
        <p className="opacity-70 mt-2">No {{.LowerCasePlural}} yet.</p>
      </main>
    );
  }

  return (
    <main className="container mx-auto p-6">
      <h1 className="text-2xl font-semibold mb-6">{{.PascalCasePlural}}</h1>
      <ul className="space-y-4">
        {items.map((it: any) => (
          <li key={it._id} className="border rounded-lg p-4">
            <h2 className="text-lg font-medium">
              <Link href={`/{{.KebabCasePageType}}/${it.slug}`}>{it.title}</Link>
            </h2>
            {it.excerpt && <p className="opacity-80 mt-1">{it.excerpt}</p>}
          </li>
        ))}
      </ul>
    </main>
  );
}

import type {Metadata, ResolvingMetadata} from 'next'
import {notFound} from 'next/navigation'
import {type PortableTextBlock} from 'next-sanity'
import {Suspense} from 'react'

import Avatar from '@/app/components/Avatar'
import CoverImage from '@/app/components/CoverImage'
import {MorePosts} from '@/app/components/Posts'
import PortableText from '@/app/components/PortableText'
import {sanityFetch} from '@/sanity/lib/live'
import { {{.LowerCasePageType}}Slugs, {{.LowerCasePageType}}BySlugQuery } from '@/sanity/lib/{{.KebabCasePageType}}.queries'
import {resolveOpenGraphImage} from '@/sanity/lib/utils'

export type Props = { params: Promise<{slug: string}> }

export async function generateStaticParams() {
  const {data} = await sanityFetch({
    query: {{.LowerCasePageType}}Slugs,
    perspective: 'published',
    stega: false,
  })
  return data
}

export async function generateMetadata(props: Props, parent: ResolvingMetadata): Promise<Metadata> {
  const params = await props.params
  const {data: doc} = await sanityFetch({
    query: {{.LowerCasePageType}}BySlugQuery,
    params,
    stega: false,
  })

  const previousImages = (await parent).openGraph?.images || []
  const ogImage = resolveOpenGraphImage(doc?.coverImage)

  return {
    authors:
      doc?.author?.firstName && doc?.author?.lastName
        ? [{name: `${doc.author.firstName} ${doc.author.lastName}`}] 
        : [],
    title: doc?.title,
    description: doc?.excerpt,
    openGraph: {
      images: ogImage ? [ogImage, ...previousImages] : previousImages,
    },
  } satisfies Metadata
}

export default async function {{.PascalCasePageType}}Page(props: Props) {
  const params = await props.params
  const [{data: doc}] = await Promise.all([
    sanityFetch({ query: {{.LowerCasePageType}}BySlugQuery, params })
  ])

  if (!doc?._id) {
    return notFound()
  }

  return (
    <>
      <div className="">
        <div className="container my-12 lg:my-24 grid gap-12">
          <div>
            <div className="pb-6 grid gap-6 mb-6 border-b border-gray-100">
              <div className="max-w-3xl flex flex-col gap-6">
                <h2 className="text-4xl font-bold tracking-tight text-gray-900 sm:text-5xl lg:text-7xl">
                  {doc.title}
                </h2>
              </div>
              <div className="max-w-3xl flex gap-4 items-center">
                {doc.author && doc.author.firstName && doc.author.lastName && (
                  <Avatar person={doc.author} date={doc.date} />
                )}
              </div>
            </div>
            <article className="gap-6 grid max-w-4xl">
              <div className="">
                {doc?.coverImage && <CoverImage image={doc.coverImage} priority />}
              </div>
              {doc?.content?.length ? (
                <PortableText className="max-w-2xl" value={doc.content as PortableTextBlock[]} />
              ) : null}
            </article>
          </div>
        </div>
      </div>
      <div className="border-t border-gray-100 bg-gray-50">
        <div className="container py-12 lg:py-24 grid gap-12">
          <aside>
            <Suspense>{await MorePosts({skip: doc._id, limit: 2})}</Suspense>
          </aside>
        </div>
      </div>
    </>
  )
}

