import { defineQuery } from "next-sanity";

export const fields = /* groq */ `
  _id,
  "title": coalesce(title, "Untitled"),
  "slug": slug.current,
  excerpt,
  hero,
  content
`;

// List (plural)
export const all{{.PascalCasePlural}}Query = defineQuery(`
  *[_type == "{{.LowerCasePageType}}" && defined(slug.current)] | order(_updatedAt desc) {
    ${fields}
  }
`);

// By slug (singular)
export const {{.LowerCasePageType}}BySlugQuery = defineQuery(`
  *[_type == "{{.LowerCasePageType}}" && slug.current == $slug][0] {
    ${fields}
  }
`);

// Slugs only
export const {{.LowerCasePageType}}Slugs = defineQuery(`
  *[_type == "{{.LowerCasePageType}}" && defined(slug.current)]{ "slug": slug.current }
`);

import {defineField, defineType} from 'sanity'
import { DocumentIcon } from '@sanity/icons'

export const {{.LowerCasePageType}} = defineType({
  name: '{{.LowerCasePageType}}',
  title: '{{.PascalCasePageType}}',
  type: 'document',
  icon: DocumentIcon,
  fields: [
    defineField({ name: 'title', title: 'Title', type: 'string', validation: r => r.required() }),
    defineField({ name: 'slug', title: 'Slug', type: 'slug', options: { source: 'title', maxLength: 96 }, validation: r => r.required() }),
    defineField({ name: 'excerpt', title: 'Excerpt', type: 'text' }),
    defineField({
      name: 'hero', title: 'Hero image', type: 'image', options: { hotspot: true, aiAssist: { imageDescriptionField: 'alt' } },
      fields: [
        defineField({ name: 'alt', title: 'Alt text', type: 'string', description: 'Important for SEO and accessibility.', validation: rule => rule.custom((alt, ctx) => { if ((ctx.document?.hero as any)?.asset?._ref && !alt) return 'Required'; return true; }) })
      ]
    }),
    defineField({ name: 'content', title: 'Content', type: 'blockContent' }),
  ],
  preview: {
    select: { title: 'title', media: 'hero' },
    prepare({ title, media }) { return { title, media } }
  }
})

