type {{.PascalCasePageTypeSingular}}ListItem = {
  _id: string
  title?: string
  name?: string
  slug: string
  excerpt?: string | null
  subheading?: string | null
  coverImage?: unknown
  date?: string
  author?:
    | {
        firstName?: string
        lastName?: string
        picture?: unknown
      }
    | null
