import type {Metadata} from 'next'
import Head from 'next/head'

import PageBuilderPage from '@/app/components/PageBuilder'
import {sanityFetch} from '@/sanity/lib/live'
import { {{.LowerCasePageType}}BySlugQuery, {{.LowerCasePageType}}Slugs } from '@/sanity/lib/{{.KebabCasePageType}}.queries'
import {PageOnboarding} from '@/app/components/Onboarding'

export type Props = { params: Promise<{slug: string}> }

export async function generateStaticParams() {
  const {data} = await sanityFetch({
    query: {{.LowerCasePageType}}Slugs,
    perspective: 'published',
    stega: false,
  })
  return data
}

export async function generateMetadata(props: Props): Promise<Metadata> {
  const params = await props.params
  const {data: page} = await sanityFetch({
    query: {{.LowerCasePageType}}BySlugQuery,
    params,
    stega: false,
  })

  return {
    title: page?.name,
    description: page?.heading,
  } satisfies Metadata
}

export default async function Page(props: Props) {
  const params = await props.params
  const [{data: page}] = await Promise.all([
    sanityFetch({ query: {{.LowerCasePageType}}BySlugQuery, params })
  ])

  if (!page?._id) {
    return (
      <div className="py-40">
        <PageOnboarding />
      </div>
    )
  }

  return (
    <div className="my-12 lg:my-24">
      <Head>
        <title>{page.heading}</title>
      </Head>
      <div className="">
        <div className="container">
          <div className="pb-6 border-b border-gray-100">
            <div className="max-w-3xl">
              <h2 className="text-4xl font-bold tracking-tight text-gray-900 sm:text-5xl lg:text-7xl">
                {page.heading}
              </h2>
              <p className="mt-4 text-base lg:text-lg leading-relaxed text-gray-600 uppercase font-light">
                {page.subheading}
              </p>
            </div>
          </div>
        </div>
      </div>
      <PageBuilderPage page={page as any} />
    </div>
  )
}

import Link from "next/link";
import type { Metadata } from "next";
import { client } from "@/sanity/lib/client";
import { all{{.PascalCasePlural}}Query } from "@/sanity/lib/{{.KebabCasePageType}}.queries";

export const metadata: Metadata = {
  title: "{{.PascalCasePlural}}",
  description: "All {{.LowerCasePlural}}"
};

export default async function {{.PascalCasePageType}}IndexPage() {
  const items = await client.fetch(all{{.PascalCasePlural}}Query);

  if (!items?.length) {
    return (
      <main className="container mx-auto p-6">
        <h1 className="text-2xl font-semibold">{{.PascalCasePlural}}</h1>
        <p className="opacity-70 mt-2">No {{.LowerCasePlural}} yet.</p>
      </main>
    );
  }

  return (
    <main className="container mx-auto p-6">
      <h1 className="text-2xl font-semibold mb-6">{{.PascalCasePlural}}</h1>
      <ul className="space-y-4">
        {items.map((it: any) => (
          <li key={it._id} className="border rounded-lg p-4">
            <h2 className="text-lg font-medium">
              <Link href={`/{{.KebabCasePageType}}/${it.slug}`}>{it.name}</Link>
            </h2>
            {it.subheading && <p className="opacity-80 mt-1">{it.subheading}</p>}
          </li>
        ))}
      </ul>
    </main>
  );
}

