  return (
    <section
      className="container my-12"
      data-sanity={dataAttr({ id: block?._id || 'unknown', type: '{{.LowerCaseBlockTypeSingular}}', path: `pageBuilder[_key==\"${block?._key}\"]` }).toString()}
    >
      <div className="bg-gray-50 border border-gray-100 rounded-2xl p-10 grid gap-6">
